<li><code>DefaultRequestProcessor</code></li>
</ul>
<div class="highlight"><pre style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-java" data-lang="java"><span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">class</span> <span style="color:#008b45;font-weight:bold">DefaultRequestProcessor</span> <span style="color:#8b008b;font-weight:bold">extends</span> AsyncNettyRequestProcessor <span style="color:#8b008b;font-weight:bold">implements</span> NettyRequestProcessor {
<span style="color:#707a7c">@Override</span>
<span style="color:#8b008b;font-weight:bold">public</span> RemotingCommand <span style="color:#008b45">processRequest</span>(ChannelHandlerContext ctx,
RemotingCommand request) <span style="color:#8b008b;font-weight:bold">throws</span> RemotingCommandException {
<span style="color:#8b008b;font-weight:bold">if</span> (ctx != <span style="color:#8b008b;font-weight:bold">null</span>) {
