request.<span style="color:#658b00">getCode</span>(),
RemotingHelper.<span style="color:#658b00">parseChannelRemoteAddr</span>(ctx.<span style="color:#658b00">channel</span>()),
request);
}
<span style="color:#8b008b;font-weight:bold">switch</span> (request.<span style="color:#658b00">getCode</span>()) {
<span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">PUT_KV_CONFIG</span>:
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">putKVConfig</span>(ctx, request); <span style="color:#228b22">// KvConfigManager 存放配置并持久化
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">GET_KV_CONFIG</span>:
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">getKVConfig</span>(ctx, request); <span style="color:#228b22">// KvConfigManager 查询配置
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">DELETE_KV_CONFIG</span>:
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">deleteKVConfig</span>(ctx, request); <span style="color:#228b22">// KvConfigManager 删除配置
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">QUERY_DATA_VERSION</span>:
<span style="color:#8b008b;font-weight:bold">return</span> queryBrokerTopicConfig(ctx, request); <span style="color:#228b22">// 查询数据的版本信息
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">REGISTER_BROKER</span>:
Version brokerVersion = MQVersion.<span style="color:#658b00">value2Version</span>(request.<span style="color:#658b00">getVersion</span>());
<span style="color:#8b008b;font-weight:bold">if</span> (brokerVersion.<span style="color:#658b00">ordinal</span>() &gt;= MQVersion.<span style="color:#658b00">Version</span>.<span style="color:#658b00">V3_0_11</span>.<span style="color:#658b00">ordinal</span>()) {
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">registerBrokerWithFilterServer</span>(ctx, request); <span style="color:#228b22">// 注册 broker
</span><span style="color:#228b22"></span> } <span style="color:#8b008b;font-weight:bold">else</span> {
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">registerBroker</span>(ctx, request);
}
<span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">UNREGISTER_BROKER</span>:
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">unregisterBroker</span>(ctx, request); <span style="color:#228b22">// 解除注册 broker
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">GET_ROUTEINFO_BY_TOPIC</span>:
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">getRouteInfoByTopic</span>(ctx, request); <span style="color:#228b22">// 根据topic 获取 routeinfo
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">GET_BROKER_CLUSTER_INFO</span>:
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">getBrokerClusterInfo</span>(ctx, request); <span style="color:#228b22">//获取 broker 信息
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">WIPE_WRITE_PERM_OF_BROKER</span>:
