</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">GET_ALL_TOPIC_LIST_FROM_NAMESERVER</span>:
<span style="color:#8b008b;font-weight:bold">return</span> getAllTopicListFromNameserver(ctx, request); <span style="color:#228b22">// 获取所有的 topic 列表
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">DELETE_TOPIC_IN_NAMESRV</span>:
<span style="color:#8b008b;font-weight:bold">return</span> deleteTopicInNamesrv(ctx, request); <span style="color:#228b22">// 删除
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">GET_KVLIST_BY_NAMESPACE</span>:
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">getKVListByNamespace</span>(ctx, request); <span style="color:#228b22">// 获取 kvlist
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">case</span> RequestCode.<span style="color:#658b00">GET_TOPICS_BY_CLUSTER</span>:
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">getTopicsByCluster</span>(ctx, request); <span style="color:#228b22">// 根据 cluster 获取 topic 列表
