<p>这边选择性的进行摘取记录描述啊</p>
<div class="highlight"><pre style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-java" data-lang="java"><span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">class</span> <span style="color:#008b45;font-weight:bold">NettyRemotingServer</span> <span style="color:#8b008b;font-weight:bold">extends</span> NettyRemotingAbstract <span style="color:#8b008b;font-weight:bold">implements</span> RemotingServer {
<span style="color:#228b22">// todo 此处代码省略号...
</span><span style="color:#228b22"></span> <span style="color:#228b22">// 构造
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#008b45">NettyRemotingServer</span>(<span style="color:#8b008b;font-weight:bold">final</span> NettyServerConfig nettyServerConfig,
<span style="color:#8b008b;font-weight:bold">final</span> ChannelEventListener channelEventListener) {
<span style="color:#228b22">//调用 NettyRemotingAbstract 的构造初始化相关配置
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">super</span>(nettyServerConfig.<span style="color:#658b00">getServerOnewaySemaphoreValue</span>(), nettyServerConfig.<span style="color:#658b00">getServerAsyncSemaphoreValue</span>());
<span style="color:#228b22">//开启一个ServerBootstrap
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">serverBootstrap</span> = <span style="color:#8b008b;font-weight:bold">new</span> ServerBootstrap();
<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">nettyServerConfig</span> = nettyServerConfig;
<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">channelEventListener</span> = channelEventListener;
<span style="color:#228b22">//默认的 React
</span><span style="color:#228b22"></span> <span style="color:#00688b;font-weight:bold">int</span> publicThreadNums = nettyServerConfig.<span style="color:#658b00">getServerCallbackExecutorThreads</span>();
<span style="color:#8b008b;font-weight:bold">if</span> (publicThreadNums &lt;= 0) {
publicThreadNums = 4;
}
<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">publicExecutor</span> = Executors.<span style="color:#658b00">newFixedThreadPool</span>(publicThreadNums, <span style="color:#8b008b;font-weight:bold">new</span> ThreadFactory() {
<span style="color:#8b008b;font-weight:bold">private</span> AtomicInteger threadIndex = <span style="color:#8b008b;font-weight:bold">new</span> AtomicInteger(0);
<span style="color:#707a7c">@Override</span>
<span style="color:#8b008b;font-weight:bold">public</span> Thread <span style="color:#008b45">newThread</span>(Runnable r) {
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">new</span> Thread(r, <span style="color:#cd5555">&#34;NettyServerPublicExecutor_&#34;</span> + <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">threadIndex</span>.<span style="color:#658b00">incrementAndGet</span>());
}
});
<span style="color:#228b22">//根据系统环境或指的配置选择使用 Epoll 还是 Nio 模式
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">if</span> (useEpoll()) {
<span style="color:#228b22">//构建 EventLoopGroup 只有1个线程
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">eventLoopGroupBoss</span> = <span style="color:#8b008b;font-weight:bold">new</span> EpollEventLoopGroup(1, <span style="color:#8b008b;font-weight:bold">new</span> ThreadFactory() {
<span style="color:#8b008b;font-weight:bold">private</span> AtomicInteger threadIndex = <span style="color:#8b008b;font-weight:bold">new</span> AtomicInteger(0);
<span style="color:#707a7c">@Override</span>
<span style="color:#8b008b;font-weight:bold">public</span> Thread <span style="color:#008b45">newThread</span>(Runnable r) {
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">new</span> Thread(r, String.<span style="color:#658b00">format</span>(<span style="color:#cd5555">&#34;NettyEPOLLBoss_%d&#34;</span>, <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">threadIndex</span>.<span style="color:#658b00">incrementAndGet</span>()));
}
});
<span style="color:#228b22">//构建 eventLoopGroupSelector
