<span style="color:#8b008b;font-weight:bold">private</span> AtomicInteger threadIndex = <span style="color:#8b008b;font-weight:bold">new</span> AtomicInteger(0);
<span style="color:#8b008b;font-weight:bold">private</span> <span style="color:#00688b;font-weight:bold">int</span> threadTotal = nettyServerConfig.<span style="color:#658b00">getServerSelectorThreads</span>();
<span style="color:#707a7c">@Override</span>
<span style="color:#8b008b;font-weight:bold">public</span> Thread <span style="color:#008b45">newThread</span>(Runnable r) {
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">new</span> Thread(r, String.<span style="color:#658b00">format</span>(<span style="color:#cd5555">&#34;NettyServerEPOLLSelector_%d_%d&#34;</span>, threadTotal, <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">threadIndex</span>.<span style="color:#658b00">incrementAndGet</span>()));
}
});
} <span style="color:#8b008b;font-weight:bold">else</span> {
<span style="color:#228b22">// todo 此处代码省略号...
</span><span style="color:#228b22"></span> }
<span style="color:#228b22">//夹在SSL上下文
</span><span style="color:#228b22"></span> loadSslContext();
}
<span style="color:#228b22">//todo 此处代码省略号...
</span><span style="color:#228b22"></span>
<span style="color:#228b22">// 核心的 启动方法
</span><span style="color:#228b22"></span> <span style="color:#707a7c">@Override</span>
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">start</span>() {
<span style="color:#228b22">//构建 Worker线程，根据配置
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">defaultEventExecutorGroup</span> = <span style="color:#8b008b;font-weight:bold">new</span> DefaultEventExecutorGroup(
nettyServerConfig.<span style="color:#658b00">getServerWorkerThreads</span>(),
<span style="color:#8b008b;font-weight:bold">new</span> ThreadFactory() {
<span style="color:#8b008b;font-weight:bold">private</span> AtomicInteger threadIndex = <span style="color:#8b008b;font-weight:bold">new</span> AtomicInteger(0);
<span style="color:#707a7c">@Override</span>
<span style="color:#8b008b;font-weight:bold">public</span> Thread <span style="color:#008b45">newThread</span>(Runnable r) {
