<span style="color:#228b22">//解码
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">static</span> RemotingCommand <span style="color:#008b45">decode</span>(<span style="color:#8b008b;font-weight:bold">final</span> <span style="color:#00688b;font-weight:bold">byte</span>[] array) {
