<span style="color:#228b22">//解码
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">static</span> RemotingCommand <span style="color:#008b45">decode</span>(<span style="color:#8b008b;font-weight:bold">final</span> ByteBuffer byteBuffer) {
<span style="color:#00688b;font-weight:bold">int</span> length = byteBuffer.<span style="color:#658b00">limit</span>();
<span style="color:#00688b;font-weight:bold">int</span> oriHeaderLen = byteBuffer.<span style="color:#658b00">getInt</span>();
<span style="color:#00688b;font-weight:bold">int</span> headerLength = getHeaderLength(oriHeaderLen);
<span style="color:#00688b;font-weight:bold">byte</span>[] headerData = <span style="color:#8b008b;font-weight:bold">new</span> <span style="color:#00688b;font-weight:bold">byte</span>[headerLength];
byteBuffer.<span style="color:#658b00">get</span>(headerData);
RemotingCommand cmd = headerDecode(headerData, getProtocolType(oriHeaderLen));
<span style="color:#00688b;font-weight:bold">int</span> bodyLength = length - 4 - headerLength;
<span style="color:#00688b;font-weight:bold">byte</span>[] bodyData = <span style="color:#8b008b;font-weight:bold">null</span>;
<span style="color:#8b008b;font-weight:bold">if</span> (bodyLength &gt; 0) {
bodyData = <span style="color:#8b008b;font-weight:bold">new</span> <span style="color:#00688b;font-weight:bold">byte</span>[bodyLength];
byteBuffer.<span style="color:#658b00">get</span>(bodyData);
}
cmd.<span style="color:#658b00">body</span> = bodyData;
<span style="color:#8b008b;font-weight:bold">return</span> cmd;
}
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">getHeaderLength</span>(<span style="color:#00688b;font-weight:bold">int</span> length) {
<span style="color:#8b008b;font-weight:bold">return</span> length &amp; 0xFFFFFF;
}
<span style="color:#228b22">//消息头解码
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">private</span> <span style="color:#8b008b;font-weight:bold">static</span> RemotingCommand <span style="color:#008b45">headerDecode</span>(<span style="color:#00688b;font-weight:bold">byte</span>[] headerData, SerializeType type) {
<span style="color:#8b008b;font-weight:bold">switch</span> (type) {
<span style="color:#8b008b;font-weight:bold">case</span> JSON:
