<span style="color:#8b008b;font-weight:bold">default</span>:
<span style="color:#8b008b;font-weight:bold">break</span>;
}
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">null</span>;
}
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">static</span> SerializeType <span style="color:#008b45">getProtocolType</span>(<span style="color:#00688b;font-weight:bold">int</span> source) {
