<span style="color:#8b008b;font-weight:bold">private</span> <span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">boolean</span> <span style="color:#008b45">isBlank</span>(String str) {
<span style="color:#00688b;font-weight:bold">int</span> strLen;
<span style="color:#8b008b;font-weight:bold">if</span> (str == <span style="color:#8b008b;font-weight:bold">null</span> || (strLen = str.<span style="color:#658b00">length</span>()) == 0) {
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">true</span>;
}
<span style="color:#8b008b;font-weight:bold">for</span> (<span style="color:#00688b;font-weight:bold">int</span> i = 0; i &lt; strLen; i++) {
<span style="color:#8b008b;font-weight:bold">if</span> (!Character.<span style="color:#658b00">isWhitespace</span>(str.<span style="color:#658b00">charAt</span>(i))) {
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">false</span>;
}
}
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">true</span>;
}
<span style="color:#228b22">//设置协议类型
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#00688b;font-weight:bold">byte</span>[] <span style="color:#008b45">markProtocolType</span>(<span style="color:#00688b;font-weight:bold">int</span> source, SerializeType type) {
<span style="color:#00688b;font-weight:bold">byte</span>[] result = <span style="color:#8b008b;font-weight:bold">new</span> <span style="color:#00688b;font-weight:bold">byte</span>[4];
result[0] = type.<span style="color:#658b00">getCode</span>();
result[1] = (<span style="color:#00688b;font-weight:bold">byte</span>) ((source &gt;&gt; 16) &amp; 0xFF);
result[2] = (<span style="color:#00688b;font-weight:bold">byte</span>) ((source &gt;&gt; 8) &amp; 0xFF);
result[3] = (<span style="color:#00688b;font-weight:bold">byte</span>) (source &amp; 0xFF);
<span style="color:#8b008b;font-weight:bold">return</span> result;
}
