<span style="color:#8b008b;font-weight:bold">public</span> CommandCustomHeader <span style="color:#008b45">decodeCommandCustomHeader</span>(
Class&lt;? <span style="color:#8b008b;font-weight:bold">extends</span> CommandCustomHeader&gt; classHeader) <span style="color:#8b008b;font-weight:bold">throws</span> RemotingCommandException {
CommandCustomHeader objectHeader;
<span style="color:#8b008b;font-weight:bold">try</span> {
objectHeader = classHeader.<span style="color:#658b00">newInstance</span>();
} <span style="color:#8b008b;font-weight:bold">catch</span> (InstantiationException e) {
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">null</span>;
} <span style="color:#8b008b;font-weight:bold">catch</span> (IllegalAccessException e) {
<span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">null</span>;
}
<span style="color:#228b22">//解码定制的扩展信息字段
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">if</span> (<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">extFields</span> != <span style="color:#8b008b;font-weight:bold">null</span>) {
Field[] fields = getClazzFields(classHeader);
<span style="color:#8b008b;font-weight:bold">for</span> (Field field : fields) {
<span style="color:#8b008b;font-weight:bold">if</span> (!Modifier.<span style="color:#658b00">isStatic</span>(field.<span style="color:#658b00">getModifiers</span>())) {
String fieldName = field.<span style="color:#658b00">getName</span>();
<span style="color:#8b008b;font-weight:bold">if</span> (!fieldName.<span style="color:#658b00">startsWith</span>(<span style="color:#cd5555">&#34;this&#34;</span>)) {
<span style="color:#8b008b;font-weight:bold">try</span> {
String value = <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">extFields</span>.<span style="color:#658b00">get</span>(fieldName);
<span style="color:#8b008b;font-weight:bold">if</span> (<span style="color:#8b008b;font-weight:bold">null</span> == value) {
<span style="color:#8b008b;font-weight:bold">if</span> (!isFieldNullable(field)) {
<span style="color:#8b008b;font-weight:bold">throw</span> <span style="color:#8b008b;font-weight:bold">new</span> RemotingCommandException(<span style="color:#cd5555">&#34;the custom field &lt;&#34;</span> + fieldName + <span style="color:#cd5555">&#34;&gt; is null&#34;</span>);
}
<span style="color:#8b008b;font-weight:bold">continue</span>;
}
field.<span style="color:#658b00">setAccessible</span>(<span style="color:#8b008b;font-weight:bold">true</span>);
String type = getCanonicalName(field.<span style="color:#658b00">getType</span>());
Object valueParsed;
<span style="color:#8b008b;font-weight:bold">if</span> (type.<span style="color:#658b00">equals</span>(STRING_CANONICAL_NAME)) {
valueParsed = value;
} <span style="color:#8b008b;font-weight:bold">else</span> <span style="color:#8b008b;font-weight:bold">if</span> (type.<span style="color:#658b00">equals</span>(INTEGER_CANONICAL_NAME_1) || type.<span style="color:#658b00">equals</span>(INTEGER_CANONICAL_NAME_2)) {
valueParsed = Integer.<span style="color:#658b00">parseInt</span>(value);
} <span style="color:#8b008b;font-weight:bold">else</span> <span style="color:#8b008b;font-weight:bold">if</span> (type.<span style="color:#658b00">equals</span>(LONG_CANONICAL_NAME_1) || type.<span style="color:#658b00">equals</span>(LONG_CANONICAL_NAME_2)) {
valueParsed = Long.<span style="color:#658b00">parseLong</span>(value);
} <span style="color:#8b008b;font-weight:bold">else</span> <span style="color:#8b008b;font-weight:bold">if</span> (type.<span style="color:#658b00">equals</span>(BOOLEAN_CANONICAL_NAME_1) || type.<span style="color:#658b00">equals</span>(BOOLEAN_CANONICAL_NAME_2)) {
valueParsed = Boolean.<span style="color:#658b00">parseBoolean</span>(value);
} <span style="color:#8b008b;font-weight:bold">else</span> <span style="color:#8b008b;font-weight:bold">if</span> (type.<span style="color:#658b00">equals</span>(DOUBLE_CANONICAL_NAME_1) || type.<span style="color:#658b00">equals</span>(DOUBLE_CANONICAL_NAME_2)) {
valueParsed = Double.<span style="color:#658b00">parseDouble</span>(value);
} <span style="color:#8b008b;font-weight:bold">else</span> {
<span style="color:#8b008b;font-weight:bold">throw</span> <span style="color:#8b008b;font-weight:bold">new</span> RemotingCommandException(<span style="color:#cd5555">&#34;the custom field &lt;&#34;</span> + fieldName + <span style="color:#cd5555">&#34;&gt; type is not supported&#34;</span>);
}
field.<span style="color:#658b00">set</span>(objectHeader, valueParsed);
} <span style="color:#8b008b;font-weight:bold">catch</span> (Throwable e) {
log.<span style="color:#658b00">error</span>(<span style="color:#cd5555">&#34;Failed field [{}] decoding&#34;</span>, fieldName, e);
}
}
}
