objectHeader.<span style="color:#658b00">checkFields</span>();
}
<span style="color:#8b008b;font-weight:bold">return</span> objectHeader;
}
<span style="color:#8b008b;font-weight:bold">private</span> Field[] <span style="color:#008b45">getClazzFields</span>(Class&lt;? <span style="color:#8b008b;font-weight:bold">extends</span> CommandCustomHeader&gt; classHeader) {
Field[] field = CLASS_HASH_MAP.<span style="color:#658b00">get</span>(classHeader);
<span style="color:#8b008b;font-weight:bold">if</span> (field == <span style="color:#8b008b;font-weight:bold">null</span>) {
field = classHeader.<span style="color:#658b00">getDeclaredFields</span>();
<span style="color:#8b008b;font-weight:bold">synchronized</span> (CLASS_HASH_MAP) {
CLASS_HASH_MAP.<span style="color:#658b00">put</span>(classHeader, field);
}
}
<span style="color:#8b008b;font-weight:bold">return</span> field;
}
<span style="color:#8b008b;font-weight:bold">private</span> <span style="color:#00688b;font-weight:bold">boolean</span> <span style="color:#008b45">isFieldNullable</span>(Field field) {
<span style="color:#8b008b;font-weight:bold">if</span> (!NULLABLE_FIELD_CACHE.<span style="color:#658b00">containsKey</span>(field)) {
Annotation annotation = field.<span style="color:#658b00">getAnnotation</span>(CFNotNull.<span style="color:#658b00">class</span>);
<span style="color:#8b008b;font-weight:bold">synchronized</span> (NULLABLE_FIELD_CACHE) {
NULLABLE_FIELD_CACHE.<span style="color:#658b00">put</span>(field, annotation == <span style="color:#8b008b;font-weight:bold">null</span>);
}
}
<span style="color:#8b008b;font-weight:bold">return</span> NULLABLE_FIELD_CACHE.<span style="color:#658b00">get</span>(field);
}
<span style="color:#8b008b;font-weight:bold">private</span> String <span style="color:#008b45">getCanonicalName</span>(Class clazz) {
String name = CANONICAL_NAME_CACHE.<span style="color:#658b00">get</span>(clazz);
<span style="color:#8b008b;font-weight:bold">if</span> (name == <span style="color:#8b008b;font-weight:bold">null</span>) {
name = clazz.<span style="color:#658b00">getCanonicalName</span>();
<span style="color:#8b008b;font-weight:bold">synchronized</span> (CANONICAL_NAME_CACHE) {
CANONICAL_NAME_CACHE.<span style="color:#658b00">put</span>(clazz, name);
}
}
<span style="color:#8b008b;font-weight:bold">return</span> name;
}
<span style="color:#228b22">// 编码
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">public</span> ByteBuffer <span style="color:#008b45">encode</span>() {
<span style="color:#228b22">// 1&gt; header length size
</span><span style="color:#228b22"></span> <span style="color:#00688b;font-weight:bold">int</span> length = 4;
<span style="color:#228b22">// 2&gt; header data length
</span><span style="color:#228b22"></span> <span style="color:#00688b;font-weight:bold">byte</span>[] headerData = <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">headerEncode</span>();
length += headerData.<span style="color:#658b00">length</span>;
<span style="color:#228b22">// 3&gt; body data length
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">if</span> (<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">body</span> != <span style="color:#8b008b;font-weight:bold">null</span>) {
length += body.<span style="color:#658b00">length</span>;
}
ByteBuffer result = ByteBuffer.<span style="color:#658b00">allocate</span>(4 + length);
<span style="color:#228b22">// length
</span><span style="color:#228b22"></span> result.<span style="color:#658b00">putInt</span>(length);
<span style="color:#228b22">// header length
</span><span style="color:#228b22"></span> result.<span style="color:#658b00">put</span>(markProtocolType(headerData.<span style="color:#658b00">length</span>, serializeTypeCurrentRPC));
<span style="color:#228b22">// header data
</span><span style="color:#228b22"></span> result.<span style="color:#658b00">put</span>(headerData);
<span style="color:#228b22">// body data;
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">if</span> (<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">body</span> != <span style="color:#8b008b;font-weight:bold">null</span>) {
result.<span style="color:#658b00">put</span>(<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">body</span>);
}
result.<span style="color:#658b00">flip</span>();
<span style="color:#8b008b;font-weight:bold">return</span> result;
}
<span style="color:#8b008b;font-weight:bold">private</span> <span style="color:#00688b;font-weight:bold">byte</span>[] <span style="color:#008b45">headerEncode</span>() {
<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">makeCustomHeaderToNet</span>();
<span style="color:#8b008b;font-weight:bold">if</span> (SerializeType.<span style="color:#658b00">ROCKETMQ</span> == serializeTypeCurrentRPC) {
<span style="color:#8b008b;font-weight:bold">return</span> RocketMQSerializable.<span style="color:#658b00">rocketMQProtocolEncode</span>(<span style="color:#8b008b;font-weight:bold">this</span>);
} <span style="color:#8b008b;font-weight:bold">else</span> {
<span style="color:#8b008b;font-weight:bold">return</span> RemotingSerializable.<span style="color:#658b00">encode</span>(<span style="color:#8b008b;font-weight:bold">this</span>);
}
}
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#00688b;font-weight:bold">void</span> <span style="color:#008b45">makeCustomHeaderToNet</span>() {
<span style="color:#8b008b;font-weight:bold">if</span> (<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">customHeader</span> != <span style="color:#8b008b;font-weight:bold">null</span>) {
Field[] fields = getClazzFields(customHeader.<span style="color:#658b00">getClass</span>());
<span style="color:#8b008b;font-weight:bold">if</span> (<span style="color:#8b008b;font-weight:bold">null</span> == <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">extFields</span>) {
<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">extFields</span> = <span style="color:#8b008b;font-weight:bold">new</span> HashMap&lt;String, String&gt;();
}
<span style="color:#8b008b;font-weight:bold">for</span> (Field field : fields) {
<span style="color:#8b008b;font-weight:bold">if</span> (!Modifier.<span style="color:#658b00">isStatic</span>(field.<span style="color:#658b00">getModifiers</span>())) {
String name = field.<span style="color:#658b00">getName</span>();
<span style="color:#8b008b;font-weight:bold">if</span> (!name.<span style="color:#658b00">startsWith</span>(<span style="color:#cd5555">&#34;this&#34;</span>)) {
Object value = <span style="color:#8b008b;font-weight:bold">null</span>;
<span style="color:#8b008b;font-weight:bold">try</span> {
field.<span style="color:#658b00">setAccessible</span>(<span style="color:#8b008b;font-weight:bold">true</span>);
value = field.<span style="color:#658b00">get</span>(<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">customHeader</span>);
} <span style="color:#8b008b;font-weight:bold">catch</span> (Exception e) {
log.<span style="color:#658b00">error</span>(<span style="color:#cd5555">&#34;Failed to access field [{}]&#34;</span>, name, e);
}
<span style="color:#8b008b;font-weight:bold">if</span> (value != <span style="color:#8b008b;font-weight:bold">null</span>) {
<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">extFields</span>.<span style="color:#658b00">put</span>(name, value.<span style="color:#658b00">toString</span>());
}
}
}
}
}
}
<span style="color:#8b008b;font-weight:bold">public</span> ByteBuffer <span style="color:#008b45">encodeHeader</span>() {
<span style="color:#8b008b;font-weight:bold">return</span> encodeHeader(<span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">body</span> != <span style="color:#8b008b;font-weight:bold">null</span> ? <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">body</span>.<span style="color:#658b00">length</span> : 0);
}
<span style="color:#8b008b;font-weight:bold">public</span> ByteBuffer <span style="color:#008b45">encodeHeader</span>(<span style="color:#8b008b;font-weight:bold">final</span> <span style="color:#00688b;font-weight:bold">int</span> bodyLength) {
<span style="color:#228b22">// 1&gt; header length size
</span><span style="color:#228b22"></span> <span style="color:#00688b;font-weight:bold">int</span> length = 4;
<span style="color:#228b22">// 2&gt; header data length
</span><span style="color:#228b22"></span> <span style="color:#00688b;font-weight:bold">byte</span>[] headerData;
headerData = <span style="color:#8b008b;font-weight:bold">this</span>.<span style="color:#658b00">headerEncode</span>();
