<h2 id="redis分布式锁">redis分布式锁</h2>
<div class="highlight"><pre style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-java" data-lang="java">
<span style="color:#8b008b;font-weight:bold">package</span> <span style="color:#008b45;text-decoration:underline">com.kezaihui.thor.biz.core.adapter.redis.lock</span>;
<span style="color:#8b008b;font-weight:bold">import</span> <span style="color:#008b45;text-decoration:underline">lombok.extern.slf4j.Slf4j</span>;
<span style="color:#8b008b;font-weight:bold">import</span> <span style="color:#008b45;text-decoration:underline">org.springframework.beans.factory.annotation.Autowired</span>;
<span style="color:#8b008b;font-weight:bold">import</span> <span style="color:#008b45;text-decoration:underline">org.springframework.data.redis.core.RedisTemplate</span>;
<span style="color:#8b008b;font-weight:bold">import</span> <span style="color:#008b45;text-decoration:underline">org.springframework.data.redis.core.script.DefaultRedisScript</span>;
<span style="color:#8b008b;font-weight:bold">import</span> <span style="color:#008b45;text-decoration:underline">org.springframework.stereotype.Component</span>;
<span style="color:#8b008b;font-weight:bold">import</span> <span style="color:#008b45;text-decoration:underline">java.util.Collections</span>;
<span style="color:#228b22">/**
</span><span style="color:#228b22"> * RedisTemplateDistributeLockUtil
</span><span style="color:#228b22"> */</span>
<span style="color:#707a7c">@Slf4j</span>
<span style="color:#707a7c">@Component</span>
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">class</span> <span style="color:#008b45;font-weight:bold">RedisTemplateDistributeLockUtil</span> {
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#8b008b;font-weight:bold">final</span> String LOCK_SCRIPT =
<span style="color:#cd5555">&#34;local key = KEYS[1]; local value = ARGV[1]; if redis.call(&#39;set&#39;, key, value, &#39;NX&#39; ,&#39;PX&#39;, %d) &#34;</span>
+ <span style="color:#cd5555">&#34;then return 1 else return 0 end&#34;</span>;
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#8b008b;font-weight:bold">final</span> String UNLOCK_SCRIPT =
<span style="color:#cd5555">&#34;if redis.call(&#39;get&#39;, KEYS[1]) == ARGV[1] then return redis.call(&#39;del&#39;,KEYS[1]) else return 0 end&#34;</span>;
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">static</span> <span style="color:#8b008b;font-weight:bold">final</span> <span style="color:#00688b;font-weight:bold">int</span> EXPIRE = 5000;
<span style="color:#707a7c">@Autowired</span>
<span style="color:#8b008b;font-weight:bold">private</span> RedisTemplate redisTemplate;
<span style="color:#8b008b;font-weight:bold">private</span> String <span style="color:#008b45">lockExpireScript</span>(<span style="color:#00688b;font-weight:bold">int</span> expireMills) {
<span style="color:#8b008b;font-weight:bold">if</span> (expireMills &lt;= 0) {
expireMills = EXPIRE;
}
<span style="color:#8b008b;font-weight:bold">return</span> String.<span style="color:#658b00">format</span>(LOCK_SCRIPT, expireMills);
}
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#00688b;font-weight:bold">boolean</span> <span style="color:#008b45">lock</span>(String key, String value, <span style="color:#00688b;font-weight:bold">int</span> expireMills) {
String script = lockExpireScript(expireMills);
DefaultRedisScript&lt;Long&gt; redisScript = <span style="color:#8b008b;font-weight:bold">new</span> DefaultRedisScript&lt;&gt;(script, Long.<span style="color:#658b00">class</span>);
Long execute = (Long) redisTemplate.<span style="color:#658b00">execute</span>(redisScript, Collections.<span style="color:#658b00">singletonList</span>(key),
Collections.<span style="color:#658b00">singletonList</span>(value));
<span style="color:#8b008b;font-weight:bold">return</span> execute != <span style="color:#8b008b;font-weight:bold">null</span> &amp;&amp; execute != 0;
}
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#00688b;font-weight:bold">boolean</span> <span style="color:#008b45">blockLock</span>(String key, String value, <span style="color:#00688b;font-weight:bold">int</span> expireMills) <span style="color:#8b008b;font-weight:bold">throws</span> DistributeLockTimeoutException {
<span style="color:#228b22">// 被阻塞的时间超过5秒就停止获取锁
</span><span style="color:#228b22"></span> <span style="color:#00688b;font-weight:bold">int</span> blockTime = expireMills;
<span style="color:#228b22">// 默认的间隔时间
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">for</span> (; ; ) {
<span style="color:#8b008b;font-weight:bold">if</span> (blockTime &gt;= 0) {
String script = lockExpireScript(expireMills);
DefaultRedisScript&lt;Long&gt; redisScript = <span style="color:#8b008b;font-weight:bold">new</span> DefaultRedisScript&lt;&gt;(script, Long.<span style="color:#658b00">class</span>);
Long result = (Long) redisTemplate.<span style="color:#658b00">execute</span>(redisScript, Collections.<span style="color:#658b00">singletonList</span>(key), value);
<span style="color:#8b008b;font-weight:bold">if</span> (result != <span style="color:#8b008b;font-weight:bold">null</span> &amp;&amp; result == 1) {
<span style="color:#228b22">// 得到了锁
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">return</span> <span style="color:#8b008b;font-weight:bold">true</span>;
} <span style="color:#8b008b;font-weight:bold">else</span> {
blockTime -= 300;
<span style="color:#8b008b;font-weight:bold">try</span> {
Thread.<span style="color:#658b00">sleep</span>(300);
} <span style="color:#8b008b;font-weight:bold">catch</span> (InterruptedException e) {
log.<span style="color:#658b00">error</span>(<span style="color:#cd5555">&#34;RedisTemplateDistributeLockUtil.blockLock error!&#34;</span>, e);
}
}
} <span style="color:#8b008b;font-weight:bold">else</span> {
<span style="color:#228b22">// 已经超时
</span><span style="color:#228b22"></span> <span style="color:#8b008b;font-weight:bold">throw</span> <span style="color:#8b008b;font-weight:bold">new</span> DistributeLockTimeoutException(<span style="color:#cd5555">&#34;Distribute Lock Timeout&#34;</span>);
}
}
}
<span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#00688b;font-weight:bold">boolean</span> <span style="color:#008b45">unlock</span>(String key, String value) {
String script = UNLOCK_SCRIPT;
DefaultRedisScript&lt;Long&gt; redisScript = <span style="color:#8b008b;font-weight:bold">new</span> DefaultRedisScript&lt;&gt;(script, Long.<span style="color:#658b00">class</span>);
Long execute = (Long) redisTemplate.<span style="color:#658b00">execute</span>(redisScript, Collections.<span style="color:#658b00">singletonList</span>(key), value);
<span style="color:#8b008b;font-weight:bold">return</span> execute != <span style="color:#8b008b;font-weight:bold">null</span> &amp;&amp; execute != 0;
}
}
</code></pre></div>- https://pinkhello.me/posts/14-%E5%B7%A5%E4%BD%9C%E7%BA%AA%E5%AE%9E2020/ - PinkHello, All Rights Reserved

